import React, { useState, Fragment, useEffect } from "react";
import { nanoid } from "nanoid";
import "./SecondyearStudent.css";
import data from "./mock-data.json";
import ReadOnlyRow from "./ReadOnlyRow";
import EditableRow from "./EditableRow";
import axios from "axios";

export const SecondyearStudent = () => {
  const [showAddForm, setShowAddform] = useState(false);
  const [contacts, setContacts] = useState([]);
  /* const [addFormData, setAddFormData] = useState({
    engname: "",
    seatno: "",
    phone_no: "",
  }); */
  const [engname, setEngname] = useState("");
  const [seatno, setSeatNo] = useState("");
  const [phone_no, setPhoneno] = useState("");

  /* useEffect(() => {
    fetchData();
  }, []);
  const fetchData = async () => {
    const response = await fetch("");
    const contact = await response.json();
    setContacts(contact);
  }; */
  //...
  /* useEffect(() => {
    const fetchDataAsync = async () => {
      try {
        const response = await fetch(
          "http://127.0.0.1:8000/student_registration/first_year"
        );
        const data = await response.json();
        console.log("Fetched data", data);
        setContacts(data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchDataAsync();
  }, []); */

  useEffect(() => {
    const fetchDataAsync = async () => {
      try {
        const response = await fetch(
          "http://127.0.0.1:8000/student_registration/first_year"
        );
        const data = await response.json();
        console.log("Fetched data", data);

        if (Array.isArray(data)) {
          // If the data is an array, update the contacts state
          setContacts(data);
        } else {
          // If the data is not an array, handle it accordingly (e.g., show an error message)
          console.error("Invalid data format");
          // Optionally, you can set contacts to an empty array to clear any existing data
          // setContacts([]);
        }
      } catch (error) {
        console.error(error);
      }
    };

    fetchDataAsync();
  }, []);
  const handleToggleAddForm = () => {
    setShowAddform(!showAddForm);
  };

  const handleCancelAddForm = () => {
    /* setAddFormData({
      engname: "",
      seatno: "",
      phone_no: "",
    }); */
    setShowAddform(false);
  };
  //...

  const [editFormData, setEditFormData] = useState({
    engname: "",
    seatno: "",
    phone_no: "",
  });

  const [editContactId, setEditContactId] = useState(null);

  const handleAddFormChange = (event) => {
    event.preventDefault();

    const fieldName = event.target.name;
    const fieldValue = event.target.value;

    /* const newFormData = { ...addFormData };
    newFormData[fieldName] = fieldValue; */

    setAddFormData({ ...addFormData, [fieldName]: fieldValue });
  };

  const handleEditFormChange = (event) => {
    event.preventDefault();

    const fieldName = event.target.getAttribute("name");
    const fieldValue = event.target.value;

    const newFormData = { ...editFormData };
    newFormData[fieldName] = fieldValue;

    setEditFormData(newFormData);
  };

  const handleAddFormSubmit = async (event) => {
    event.preventDefault();
    const formData = new FormData();
    formData.append("engname", engname);
    formData.append("seatno", seatno);
    formData.append("phone_no", phone_no);

    try {
      await fetch("http://127.0.0.1:8000/student_registration/first_year", {
        method: "POST",
        body: formData,
      });
      /* const newContact = { id: nanoid(), engname, seatno, phone_no };
      setContacts([...contacts, newContact]); */

      setEngname("");
      setSeatNo("");
      setPhoneno("");
      //fetchData();
    } catch (error) {
      console.error(error);
    }

    /* handleCancelAddForm(); */
    /* const newContacts = [...contacts, newContact];
    setContacts(newContacts); */
  };

  const handleEditFormSubmit = (event) => {
    event.preventDefault();

    const editedContact = {
      id: editContactId,
      engname: editFormData.engname,
      seatno: editFormData.seatno,
      phone_no: editFormData.phone_no,
    };

    const newContacts = [...contacts];

    const index = contacts.findIndex((contact) => contact.id === editContactId);

    newContacts[index] = editedContact;

    setContacts(newContacts);
    setEditContactId(null);
  };

  const handleEditClick = (event, contact) => {
    event.preventDefault();
    setEditContactId(contact.id);

    const formValues = {
      engname: contact.engname,
      seatno: contact.seatno,
      phone_no: contact.phone_no,
    };

    setEditFormData(formValues);
  };

  const handleCancelClick = () => {
    setEditContactId(null);
  };

  const handleDeleteClick = (contactId) => {
    const newContacts = [...contacts];

    const index = contacts.findIndex((contact) => contact.id === contactId);

    newContacts.splice(index, 1);

    setContacts(newContacts);
  };

  return (
    <div className="table">
      <div class="col-sm-3 mt-5 mb-4 text-gred">
        <div className="search">
          <input
            class="form-control mr-sm-2"
            type="search"
            placeholder="Search Student"
            aria-label="Search"
          />
        </div>
      </div>
      <button onClick={handleToggleAddForm}>
        {showAddForm ? "Cancle" : "Add a Contact"}
      </button>
      <form onSubmit={handleEditFormSubmit}>
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Roll-no</th>
              <th>Phone-no</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {contacts.map((contact) => (
              <tr key={contact.id}>
                <td>{contact.engname}</td>
                <td>{contact.seatno}</td>
                <td>{contact.phone_no}</td>
              </tr>
              /* <Fragment>
                {editContactId === contact.id ? (
                  <EditableRow
                    editFormData={editFormData}
                    handleEditFormChange={handleEditFormChange}
                    handleCancelClick={handleCancelClick}
                  />
                ) : (
                  <ReadOnlyRow
                    contact={contact}
                    handleEditClick={handleEditClick}
                    handleDeleteClick={handleDeleteClick}
                  />
                )}
              </Fragment> */
            ))}
          </tbody>
        </table>
      </form>

      <h2>Add a Contact</h2>
      {showAddForm && (
        <form onSubmit={handleAddFormSubmit}>
          <input
            type="text"
            name="engname"
            value={engname}
            required="required"
            placeholder="Enter a name..."
            onChange={(e) => setEngname(e.target.value)}
          />
          <input
            type="text"
            name="seatno"
            value={seatno}
            required="required"
            placeholder="Enter roll no..."
            onChange={(e) => setSeatNo(e.target.value)}
          />
          <input
            type="text"
            name="phone_no"
            value={phone_no}
            required="required"
            placeholder="Enter a phone number..."
            onChange={(e) => setPhoneno(e.target.value)}
          />

          <button type="submit">Add</button>
          <button type="button" onClick={handleCancelAddForm}>
            Cancle
          </button>
        </form>
      )}
    </div>
  );
};

export default SecondyearStudent;
